import React, { useEffect, useState } from 'react'

function Myinput({ socket, userId, username, friendId, setOnlineUsers }) {
    const [msg, setmsg] = useState("");

    const readInput = (e) => {
        setmsg(e.target.value);
    }

    useEffect(() => {
        socket.on("getusers", (users) => {
            setOnlineUsers([users]);
        });
    }, [socket, setOnlineUsers])
    useEffect(() => {
        socket.on('privatemessage', (pvtMsg) => {
            if (pvtMsg.authorId === foundUser._id || pvtMsg.authorId === userId) {
                setNewestSocketPrivateMessage(pvtMsg);
            }
        });
    }, [foundUser._id, userId]);
    const submitHandler = (e) => {
        e.preventDefault();
        if (msg.length > 0) {
            socket.emit("privatemsg", {
                content: msg,
                username: username,
                userId: userId,
                receiver: friendId
            });
            socket.emit("adduser", userId, username);
        } else {
            alert("Message Shouldn't be Empty!")
        }
        setmsg("");
    }

    return (
        <form onSubmit={submitHandler}>
            <div className="sendmsgForm" >
                <div className="sendFormInner" >
                    <input value={msg} disabled={friendId ? false : true} autoComplete="off" onChange={readInput} name="inputMsg" type="text" placeholder="Write here...." />
                    <button name="inputMsg" type="submit"> <i className="fa fa-send"></i></button>
                </div>
            </div>
        </form>
    )
}

export default Myinput
