import React, { useEffect, useRef } from 'react'
import { useSelector } from 'react-redux';
import { Link } from 'react-router-dom';

function UsersList({ foundUser, onlineUsers }) {
    let userID = useSelector(state => state.user_reducer.user);
    const username = useSelector(state => state.user_reducer.username);
    let newOnlineUsers = [];
    newOnlineUsers.push(onlineUsers[0]);
    let ref = useRef(null);
    ref.current = newOnlineUsers;
    console.log(ref.current[0][0], foundUser);
    useEffect(() => {
        if (ref.current[0][0] && foundUser._id) {
            let bool = ref.current[0].some((elem) => (
                elem.userId === foundUser._id
            ))
            if (!bool) {
                ref.current[0].push({ "userId": foundUser._id, "username": foundUser.name });
            }
            console.log(ref.current);
        }
    }, [foundUser.name, foundUser._id, ref.current])

    return (
        <div className="leftUsersContainer">
            {
                ref.current[0] && ref.current[0].length > 0 ?
                    ref.current[0].map((elem, index) => (
                        elem.userId && elem.userId !== userID ?
                            <Link key={index} to={`/chat/${elem.userId}`}>
                                <div className="leftUserContent">
                                    <span>
                                        <i className="fa fa-user-circle-o userIcon"></i>
                                        <h3>{elem.username}</h3>
                                    </span>
                                    <i className={elem.socketId ? "fa fa-circle active" : " "}></i>
                                </div>
                            </Link>
                            :

                            elem.userId && (
                                <div key={index} className="leftUserContent ">
                                    <span >
                                        <span className="tooltiptext">Talking To Oneself Not Allowed!!</span>
                                        <i className="fa fa-user-circle-o userIcon "></i>
                                        <h3>{username}(Me)</h3>
                                    </span>
                                    <i className="fa fa-circle active"></i>
                                </div>
                            ))
                    )
                    :
                    ""
            }
        </div >
    )
}

export default UsersList
