import React, { useState, useEffect } from 'react'
import { useSelector } from 'react-redux';
import { SERVER, SERVER_URL } from './config';
import UsersList from './usersList'
import axios from 'axios'
import ShowMessages from './showMessages';
import Myinput from './myinput';
import io from 'socket.io-client'

let socket;
function ChatBox({ match }) {
    const [state, setstate] = useState("");
    const [onlineUsers, setOnlineUsers] = useState([]);
    const userId = useSelector(state => state.user_reducer.user);
    const username = useSelector(state => state.user_reducer.username);
    const [newestSocketPrivateMessage, setNewestSocketPrivateMessage] = useState(null);
    const [foundUser, setFoundUser] = useState({});
    const [foundMessages, setFoundMessages] = useState([]);
    if (match) {
        var friendId = match.params.id;
    }
    socket = io(SERVER);
    // add User
    useEffect(() => {
        const handleEvent = (users) => setOnlineUsers([users]);
        socket.on("getusers", handleEvent);
    }, []);

    useEffect(() => {
        if (socket) {
            socket.on('privatemessage', pm => {
                if (pm.authorId === foundUser._id || pm.authorId === userId) {
                    setNewestSocketPrivateMessage(pm);
                }
            });
        }
        return () => {
            if (socket) {
                socket.removeAllListeners();
            }
        };
    }, [socket, foundUser._id, userId]);
    useEffect(() => {
        try {
            let isSubscribed = true;
            const findUser = async () => {
                const returnedUser = await axios.get(`${SERVER_URL}/userinfo/${friendId}`);
                if (returnedUser && isSubscribed) {
                    setFoundUser(returnedUser.data);
                }
            };
            findUser();

            const loadPrivateMessages = async () => {
                const foundMessages = await axios.get(
                    `${SERVER_URL}/privateconvo?userid=${userId}&friendid=${friendId}`
                );
                if (foundMessages.data.length > 0 && isSubscribed) {
                    setFoundMessages(foundMessages.data);
                }
            };
            loadPrivateMessages();
            return () => (isSubscribed = false);

        } catch (error) {
            console.log(error);
        }
    }, [friendId, userId]);



    useEffect(() => {
        if (newestSocketPrivateMessage) {
            setFoundMessages(existingMessages => [
                ...existingMessages,
                newestSocketPrivateMessage
            ]);
        }
    }, [newestSocketPrivateMessage]);
    // useEffect(() => {
    //     if (socket) {
    //         socket.emit("updateOnlineUsers");
    //     }
    //     return (() => {
    //         if (socket) {
    //             socket.removeAllListeners()
    //         }
    //     })
    // }, [])
    if (userId) {
        return (
            <div className="chatContainer">
                <div className="chatContent">
                    <UsersList onlineUsers={onlineUsers} foundUser={foundUser} foundMessages={foundMessages} />
                    <div className="rightChatContainer">
                        <ShowMessages foundMessages={foundMessages} userId={userId} friendId={friendId} foundUser={foundUser} />
                        <Myinput socket={socket} userId={userId} username={username} friendId={friendId} foundUser={foundUser} setOnlineUsers={setOnlineUsers} setNewestSocketPrivateMessage={setNewestSocketPrivateMessage} />
                    </div>
                </div >
            </div >

        )
    } else {
        //i used settimeout for not showing content immediately after re render...... during component re-render untill fetching userId this content might be visible between renders....!!
        setTimeout(() => {
            setstate("Session Expired Please LogIn Again!!!");
        }, 500);
        return (
            <div className="chatContainer"><h2>{state}</h2> </div>
        )
    }
}

export default ChatBox
