import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { SERVER_URL } from '../components/config';
function Allusers() {

    const [users, setUsers] = useState([]);
    const userId = useSelector(state => state.user_reducer.user);
    useEffect(() => {
        async function func() {
            let response = await axios.get(`${SERVER_URL}/getallusers`, { withCredentials: true });
            setUsers(response.data.users)
        }
        func();
    }, [userId])
    return (
        <div className="allUserContainer">
            <div className="allUserContent">
                {
                    users.map((element, index) => (
                        <Link to="/chat/:">
                            <div key={index} className="userContent">
                                <span className="forUser"><i className="fa fa-user " ></i></span>
                                <h3>{element.name}</h3>
                                <span className="forActiveUser"><i className={userId === element._id ? "fa fa-circle active" : "fa fa-circle"}></i></span>
                            </div>
                        </Link>
                    ))
                }
            </div>
        </div>
    )
}

export default Allusers
