import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux';
import { Link } from 'react-router-dom';

function UsersList({ foundUser, onlineUsers }) {
    let userID = useSelector(state => state.user_reducer.user);
    const username = useSelector(state => state.user_reducer.username);
    const [newOnlineUsers, setnewOnlineUsers] = useState([]);
    // console.log(onlineUsers[0], foundUser);
    useEffect(() => {
        if (foundUser._id && onlineUsers[0]) {
            setnewOnlineUsers([
                onlineUsers[0]
            ])
            // console.log(newOnlineUsers);
            let bool = newOnlineUsers.some((elem) => (
                elem.userId === foundUser._id
            ))
            if (!bool) {
                setnewOnlineUsers(...newOnlineUsers, { "userId": foundUser._id, "username": foundUser.name });
            }
        }
    }, [foundUser.name, foundUser, onlineUsers])


    return (
        <div className="leftUsersContainer">
            {
                newOnlineUsers.length > 0 ?
                    newOnlineUsers.map((elem, index) => (
                        elem.userId && elem.userId !== userID ?
                            <Link key={index} to={`/chat/${elem.userId}`}>
                                <div className="leftUserContent">
                                    <span>
                                        <i className="fa fa-user-circle-o userIcon"></i>
                                        <h3>{elem.username}</h3>
                                    </span>
                                    <i className={elem.socketId ? "fa fa-circle active" : " "}></i>
                                </div>
                            </Link>
                            :

                            elem.userId && (
                                <Link key={index} to='/chat'>
                                    <div className="leftUserContent ">
                                        <span >
                                            <span className="tooltiptext">Talking To Oneself Not Allowed!!</span>
                                            <i className="fa fa-user-circle-o userIcon "></i>
                                            <h3>{username}(Me)</h3>
                                        </span>
                                        <i className="fa fa-circle active"></i>
                                    </div>
                                </Link>
                            ))
                    )
                    :
                    ""
            }
        </div >
    )
}

export default UsersList
