import React, { useState } from 'react'
import axios from 'axios'
import { SERVER_URL } from './config';
function Myinput({ socket, userId, username, foundUser, setFoundMessages, foundMessages }) {
    const [msg, setmsg] = useState("");
    const readInput = (e) => {
        setmsg(e.target.value);
    }
    const submitHandler = async (e) => {
        e.preventDefault();
        try {
            if (msg.length > 0) {
                const res = await axios.post(`${SERVER_URL}/messages`, message);
                setFoundMessages([...foundMessages, res.data]);
                const message = {
                    author: username,
                    authorId: userId,
                    content: msg,
                    receiver: foundUser._id,
                    participants: [userId, foundUser._id],
                }
                socket.emit("privatemsg", message);
                setmsg("");
            } else {
                alert("Message Shouldn't be Empty!")
            }
        } catch (err) {
            console.log(err);
        }



    }

    return (
        <form onSubmit={submitHandler}>
            <div className="sendmsgForm" >
                <div className="sendFormInner" >
                    <input value={msg} disabled={foundUser._id ? false : true} autoComplete="off" onChange={readInput} name="inputMsg" type="text" placeholder="Write here...." />
                    <button name="inputMsg" type="submit"> <i className="fa fa-send"></i></button>
                </div>
            </div>
        </form>
    )
}

export default Myinput
