import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { useDispatch } from 'react-redux'
import { getAllUsers } from './actions/user_actions';
function Allusers() {
    const dispatch = useDispatch();
    const [users, setUsers] = useState([]);
    const userId = useSelector(state => state.user_reducer.user);
    const onlineUsers = useSelector(state => state.user_reducer.onlineUsers);

    useEffect(() => {
        async function func() {
            let response = await dispatch(getAllUsers());
            setUsers(response.payload.data.users);
        }
        func();
    }, [userId, dispatch])
    return (
        <div className="allUserContainer">
            <div className="allUserContent">
                {
                    onlineUsers ?
                        onlineUsers[0].map((element, index) => (
                            element.userId ?
                                <Link key={index} to={`/chat/${element.userId}`}>
                                    <div className="userContent">
                                        <span className="forUser"><i className="fa fa-user " ></i></span>
                                        <h3>{element.name}</h3>
                                        <span className="forActiveUser"><i className={userId === element._id ? "fa fa-circle active" : "fa fa-circle"}></i></span>
                                    </div>
                                </Link>
                                :
                                ""
                        ))
                        :
                        ""
                }
            </div>
        </div>
    )
}

export default Allusers
