import React, { useEffect, useRef } from 'react'
import { useSelector } from 'react-redux';
import { Link } from 'react-router-dom';

function UsersList(props) {
    let userID = useSelector(state => state.user_reducer.user);
    const username = useSelector(state => state.user_reducer.username);
    var ref = useRef(null);
    ref.current = props;


    useEffect(() => {
        let isSubscribed = true;
        if (ref.current.foundUser._id && isSubscribed) {
            let bool = ref.current.onlineUsers[0].some((elem) => (
                elem.userId === ref.current.foundUser._id
            ))
            if (!bool) {
                ref.current.onlineUsers[0].push({ "userId": ref.current.foundUser._id, "username": ref.current.foundUser.name });
            }
            console.log(ref.current);
        }
        return () => (isSubscribed = false);
    }, [ref.current.foundUser.name])

    return (
        <div className="leftUsersContainer">
            {
                ref.current.onlineUsers.length > 0 ?
                    ref.current.onlineUsers[0].map((elem, index) => (
                        elem.userId && elem.userId !== userID ?
                            <Link key={index} to={`/chat/${elem.userId}`}>
                                <div className="leftUserContent">
                                    <span>
                                        <i className="fa fa-user-circle-o userIcon"></i>
                                        <h3>{elem.username}</h3>
                                    </span>
                                    <i className={elem.socketId ? "fa fa-circle active" : " "}></i>
                                </div>
                            </Link>
                            :

                            elem.userId && (
                                <Link key={index} to='/chat'>
                                    <div className="leftUserContent ">
                                        <span >
                                            <span className="tooltiptext">Talking To Oneself Not Allowed!!</span>
                                            <i className="fa fa-user-circle-o userIcon "></i>
                                            <h3>{username}(Me)</h3>
                                        </span>
                                        <i className="fa fa-circle active"></i>
                                    </div>
                                </Link>
                            ))
                    )
                    :
                    ""
            }

        </div >
    )
}

export default UsersList
