import React, { useState } from 'react'
import axios from 'axios'
function Myinput({ socket, userId, username, foundUser, data }) {
    const [msg, setmsg] = useState("");
    console.log(data)
    const readInput = (e) => {
        setmsg(e.target.value);
    }
    const submitHandler = async (e) => {
        e.preventDefault();
        if (msg.length > 0) {
            socket.emit("privatemsg", {
                content: msg,
                username: username,
                userId: userId,
                receiver: foundUser._id
            });
            try {
                const res = await axios.post("/messages", message);
                setMessages([...messages, res.data]);
            } catch (err) {
                console.log(err);
            }
        } else {
            alert("Message Shouldn't be Empty!")
        }
        setmsg("");
    }

    return (
        <form onSubmit={submitHandler}>
            <div className="sendmsgForm" >
                <div className="sendFormInner" >
                    <input value={msg} disabled={foundUser._id ? false : true} autoComplete="off" onChange={readInput} name="inputMsg" type="text" placeholder="Write here...." />
                    <button name="inputMsg" type="submit"> <i className="fa fa-send"></i></button>
                </div>
            </div>
        </form>
    )
}

export default Myinput
