import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { useDispatch } from 'react-redux'
import { getAllMessages } from './actions/user_actions';

function UsersList({ foundUser, onlineUsers }) {
    let userID = useSelector(state => state.user_reducer.user);
    const dispatch = useDispatch();
    let users = [{
        userId: " ",
        username: " "
    }];
    const username = useSelector(state => state.user_reducer.username);
    const [onlineFriends, setonlineFriends] = useState([]);
    useEffect(() => {
        async function func() {
            let response = await dispatch(getAllMessages());
            // let unique = [...new Set(response.payload.data.messages.flatMap(item => {
            //     if (item.receiver === userID) {
            //         return ({ author: `${item.author}`, authorId: `${item.authorId}` });
            //     } else {
            //         return null;
            //     }
            // }))]
            let ids = response.payload.data.messages.map(o => o.receiver)
            let filtered = response.payload.data.messages.filter((item, index) => ids.includes(item.receiver, index + 1))
            filtered = filtered.map(item => item.receiver)
            // const arr1 = getUniqueListBy(arr, 'place')

            // users = unique.map((item, index, arr) => {
            //     if (item !== null && index % 2 !== 0) {
            //         return [users[0].userId = arr[index + 1], users[0].username = item]
            //     }
            // });
            console.log(filtered, userID)
        }
        func();
    }, [dispatch, userID])

    useEffect(() => {
        if (onlineUsers.length > 0) {
            let bool = onlineUsers[0].some((elem) => (
                elem.userId === foundUser._id
            ))
            if (!bool) {
                onlineUsers[0].push({ "userId": foundUser._id, "username": foundUser.name })
                setonlineFriends([...onlineUsers[0]]);
            }
        }
    }, [onlineUsers, foundUser]);


    return (
        <div className="leftUsersContainer">
            {
                onlineUsers.length > 0 && onlineFriends ?
                    onlineUsers[0].map((elem, index) => (
                        elem.userId && elem.userId !== userID ?
                            <Link key={index} to={`/chat/${elem.userId} `}>
                                <div className={elem.userId === foundUser._id ? "leftUserContent userIsActive" : "leftUserContent"}>
                                    <span>
                                        <i className="fa fa-user-circle-o userIcon"></i>
                                        <h3>{elem.username}</h3>
                                    </span>
                                    <i className={elem.socketId ? "fa fa-circle active" : " "}></i>
                                </div>
                            </Link>
                            :

                            elem.userId && (
                                <Link key={index} to={`/chat`}>
                                    <div className="leftUserContent ">
                                        <span >
                                            <span className="tooltiptext">Talking To Oneself Not Allowed!!</span>
                                            <i className="fa fa-user-circle-o userIcon "></i>
                                            <h3>{username}(Me)</h3>
                                        </span>
                                        <i className="fa fa-circle active"></i>
                                    </div>
                                </Link>
                            ))
                    )
                    :
                    ""
            }
            {
                // users.length > 0 ?
                //     users.map((item, index, arr) => (
                //         item !== null ?
                //             <Link key={index} to={`/chat/${elem.userId} `}>
                //                 <div className={elem.userId === foundUser._id ? "leftUserContent userIsActive" : "leftUserContent"}>
                //                     <span>
                //                         <i className="fa fa-user-circle-o userIcon"></i>
                //                         <h3>{elem.username}</h3>
                //                     </span>
                //                     <i className={elem.socketId ? "fa fa-circle active" : " "}></i>
                //                 </div>
                //             </Link>
                //             :
                //             ""
                //     ))
                //     :
                //     ""
            }
        </div >
    )
}

export default UsersList
