import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux';
import { SERVER_URL } from '../components/config';
function Allusers() {

    const [state, setstate] = useState("fa fa-circle");
    let response = axios.get(`${SERVER_URL}/getallusers`, { withCredentials: true })
    const isUserActive = useSelector(state => state.user_reducer.user);
    useEffect(() => {
        if (isUserActive) {
            setstate(prev => prev + " active")
        }
    }, [isUserActive])
    return (
        <div className="allUserContainer">
            <div className="allUserContent">
                <div className="userContent">
                    <span className="forUser"><i className="fa fa-user " ></i></span>
                    <h3>Nishant Rana </h3>
                    <span className="forActiveUser"><i className={state}></i></span>
                </div>

                <div className="userContent">
                    <span className="forUser"><i className="fa fa-user " ></i></span>
                    <h3>Nishant Rana</h3>
                    <span className="forActiveUser"><i className={state}></i></span>
                </div>

            </div>
        </div>
    )
}

export default Allusers
