import React, { useState, useEffect } from 'react'
import io from 'socket.io-client';
import { useSelector } from 'react-redux';
import { SERVER, SERVER_URL } from './config';
import UsersList from './usersList'
import axios from 'axios'
import Typist from 'react-typist';

const socket = io(SERVER);

function ChatBox(props) {
    const [msg, setmsg] = useState("");
    const [onlineUsers, setOnlineUsers] = useState([]);
    const userId = useSelector(state => state.user_reducer.user);
    const username = useSelector(state => state.user_reducer.username);
    const readInput = (e) => {
        setmsg(e.target.value);
    }
    if (props.props) {
        var friendId = props.props.match.params.id;
    } else {
        friendId = props.match.params.id;
    }
    const [foundUser, setFoundUser] = useState({});
    const [foundMessages, setFoundMessages] = useState([]);

    // add User
    useEffect(() => {
        socket.emit("adduser", userId, username);
        socket.on("getusers", (users) => {
            setOnlineUsers([users]);
        })
    }, [userId, username])

    useEffect(() => {
        try {
            let isSubscribed = true;
            const findUser = async () => {
                if (friendId) {
                    const returnedUser = await axios.get(`${SERVER_URL}/userinfo/${friendId}`);
                    if (returnedUser && isSubscribed) {
                        setFoundUser(returnedUser.data);
                    }
                }
            };
            findUser();

            const loadPrivateMessages = async () => {
                if (foundUser) {

                    const foundMessages = await axios.get(
                        `${SERVER_URL}/privateconvo?userid=${userId}&friendid=${friendId}`
                    );
                    if (foundMessages.data.length > 0 && isSubscribed) {
                        setFoundMessages(foundMessages.data);
                        // scrollToBottom();
                    }// else {
                    //     setMoreMessagesExist(false);
                    // }
                }
            };
            loadPrivateMessages();
            return () => (isSubscribed = false);

        } catch (error) {
            console.log(error);
        }
    }, [foundUser, friendId, userId]);


    useEffect(() => {
        if (socket) {
            socket.emit("updateAndSendOnlineUsers");
        }
        return () => (
            socket.removeAllListeners()
        )
    }, [])

    const submitHandler = (e) => {
        e.preventDefault();
        console.log(msg)

    }
    if (userId) {
        return (
            <div className="chatContainer">
                <div className="chatContent">
                    <UsersList onlineUsers={onlineUsers} foundUser={foundUser} />
                    <div className="rightChatContainer">
                        <div className="rightChatContent">
                            {
                                foundMessages.length !== 0 ?
                                    <>
                                        <div className="left">
                                            <h4> <i className="fa fa-user-circle"></i> Nishant Rana</h4>
                                            <p>hello vikrant good morning!! </p>
                                        </div>
                                        <div className="right">

                                            <h4> <i className="fa fa-user-circle"></i> {username}</h4>
                                            <p value={msg}></p>
                                        </div>
                                    </>
                                    :
                                    friendId ?
                                        <h3>Lets Start Conversation with "<b>{foundUser.name}"</b></h3>
                                        :
                                        <Typist cursor={
                                            {
                                                hideWhenDone: true
                                            }
                                        } >

                                            <div className="typistContainer">
                                                <p className='chatHeadTxt'>Let's Chat </p>
                                                <p> Let's Talk Virtual</p>
                                                <p className='chatHeadTxt'>It's An Online live Conversation</p>
                                                <Typist.Delay ms={1000} />
                                            </div>
                                        </Typist>
                            }
                        </div>
                        <form onSubmit={submitHandler}>
                            <div className="sendmsgForm" >
                                <div className="sendFormInner" >
                                    <input disabled={friendId ? false : true} autoComplete="off" onChange={readInput} name="inputMsg" type="text" placeholder="Write here...." />
                                    <button name="inputMsg" type="submit"> <i className="fa fa-send"></i></button>
                                </div>
                            </div>
                        </form>
                    </div >
                </div >
            </div >

        )
    } else {
        setTimeout(() => {
            setstate("Session Expired Please LogIn Again!!!");
        }, 1000);
        return (
            <div className="chatContainer"><h2>{state}</h2> </div>
        )
    }
}

export default ChatBox
