import React, { useEffect, useRef } from 'react'
import { useSelector } from 'react-redux';
import { Link } from 'react-router-dom';

function UsersList(props) {
    let userID = useSelector(state => state.user_reducer.user);
    const username = useSelector(state => state.user_reducer.username);
    let ref = useRef(props);
    console.log(ref.current, props);
    // useEffect(() => {
    //     async function fetchData() {
    //         if (props.onlineUsers[0]) {
    //             ref = await props.onlineUsers[0].push({ "userId": "Nishant Rana" });
    //             console.log(ref.current);
    //         }
    //     }
    //     fetchData()
    // }, [ref])
    let count = 0;
    return (
        <div className="leftUsersContainer">
            {
                props.onlineUsers[0] ?
                    props.onlineUsers[0].map((elem, index) => (
                        elem.userId && elem.userId !== userID ?
                            <Link key={index} to={`/chat/${elem.userId}`}>
                                <div className="leftUserContent">
                                    <span>
                                        <i className="fa fa-user-circle-o userIcon "></i>
                                        <h3>{elem.username}</h3>
                                    </span>
                                    <i className="fa fa-circle active"></i>
                                </div>
                            </Link>
                            :

                            elem.userId && (
                                <div key={elem.userId} className="leftUserContent">
                                    <span>
                                        <i className="fa fa-user-circle-o userIcon "></i>
                                        <h3>{username}</h3>
                                    </span>
                                    <i className="fa fa-circle active"></i>
                                </div>
                            )
                    ))
                    :
                    ""
            }
            {
                props.onlineUsers[0] ?
                    props.onlineUsers[0].map(elem => (
                        props.foundUser._id === elem.userId ?
                            <div key={elem.userId} style={{ display: "none" }}>
                                {count = count + 1}
                            </div>
                            :
                            ""
                    ))
                    :
                    ""
            }
            {
                count === 0 && props.foundUser.name ?
                    <div key={props.foundUser._id} className="leftUserContent">
                        <span>
                            <i className="fa fa-user-circle-o userIcon "></i>
                            <h3>{props.foundUser.name}</h3>
                        </span>
                        <i className="fa fa-circle active"></i>
                    </div>
                    :
                    ""
            }
        </div >
    )
}

export default UsersList
