import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux';
import { SERVER_URL } from '../components/config';
function Allusers() {

    const [state, setstate] = useState("fa fa-circle");
    const [users, setUsers] = useState([]);
    const isUserActive = useSelector(state => state.user_reducer.user);
    useEffect(() => {
        async function func() {
            if (isUserActive) {
                setstate(prev => prev + " active")
                let response = await axios.get(`${SERVER_URL}/getallusers`, { withCredentials: true });
                setUsers({
                    users: response.data.users
                })
            }
        }
        func();
    }, [isUserActive])
    return (
        <div className="allUserContainer">
            <div className="allUserContent">
                {
                    users.map((element, index) => (
                        <div key={index} className="userContent">
                            <span className="forUser"><i className="fa fa-user " ></i></span>
                            <h3>{element.name}</h3>
                            <span className="forActiveUser"><i className={state}></i></span>
                        </div>
                    ))
                }
            </div>
        </div>
    )
}

export default Allusers
