import axios from 'axios';
import {
    LOGIN_USER,
    REGISTER_USER,
    AUTH_USER,
    LOGOUT_USER,
} from './types';

import { SERVER_URL } from '../config';

//async() is thunk function for delay
export const registerUser = (dataToSubmit) => async (dispatch) => {
    let responseData = await axios.post(`${SERVER_URL}/register`, dataToSubmit)
    return dispatch({
        type: REGISTER_USER,
        payload: responseData
    })
}
export const loginUser = (dataToSubmit) => async (dispatch) => {
    const resData = await axios.post(`${SERVER_URL}/login`, dataToSubmit, {
        withCredentials: true,
    });
    return dispatch({
        type: LOGIN_USER,
        payload: resData
    })
}
